import logo from './logo.svg';
import './App.css';
import Button from 'react-bootstrap/Button';
import { useState, useReducer } from 'react';
import Landing from './components/Landing';
var lonvar=10;
var latvar=10;
function App() {


  // navigate to link if within range 



  const fireevent = () => {
    // alert("");
    // epi center link
    // https://ashmi275.github.io/location-based-augumented-reality/index.html

    console.log((Math.abs(latvar)-Math.abs(42.3111023))); 
    console.log(Math.abs(lonvar)-Math.abs(-83.0375344));

    if(((Math.abs(latvar)-Math.abs(42.3111023))>=0 && (Math.abs(latvar)-Math.abs(42.3111023))<=0.001) || ((Math.abs(latvar)-Math.abs(42.3111023))<=0 && (Math.abs(latvar)-Math.abs(42.3111023))>=-(0.001))){
      if(((Math.abs(lonvar)-Math.abs(-83.037532))>=0 && (Math.abs(lonvar)-Math.abs(-83.037532))<=0.001) || ((Math.abs(lonvar)-Math.abs(-83.037532))<=0 && (Math.abs(lonvar)-Math.abs(-83.037532))>=(-0.001))){
        window.open("https://ashmi275.github.io/location-based-augumented-reality/index.html", "_blank")
      }
    }else{
      window.alert("not at that location");
    }

    // (((Math.abs(lonvar)-Math.abs(43.1111018))>=0)  )? ( 

    //   console.log(console.log("not working"))

    // ):( console.log(console.log("not working")))


  }

  return (
    <div className="App">
      {
        navigator.geolocation.getCurrentPosition(function(position) {
          console.log("Latitude is :", position.coords.latitude);
           console.log("Longitude is :", position.coords.longitude);
           latvar=position.coords.latitude;
           lonvar=position.coords.longitude;
         }

         )

      }

 <Button onClick={fireevent} variant="primary" size="lg">
    Click to open VR camera
  </Button>



    </div>
  );
}

export default App;
